import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios'
export class UpdateUser extends Component {
    constructor(props) {
        super(props)

        // State
        this.state = {
            username: '',
            first_name: '',
            last_name: '',
            email: '',
            date_joined:null
        }
    }

    componentDidMount() {
        axios.get(`http://127.0.0.1:8000/auth/user/` + this.props.match.params.id + `/`).then(res => {
            this.setState({ username: res.data.username, first_name: res.data.first_name, last_name: res.data.last_name, email: res.data.email});
        }).catch((error) => {
            console.log(error);
        })
    }
    handleChangeuserInformations(event) {
        const { name, value } = event.target;
        this.setState({ ...this.state, [name]: value });
    }
    onSubmit = (e) => {
        e.preventDefault()

        const taskObject = {
            username: this.state.username,
            first_name: this.state.first_name,
            last_name: this.state.last_name,
            email: this.state.email,
            date_joined:this.state.date_joined
            
        };
        axios.put(`http://127.0.0.1:8000/auth/user/` + this.props.match.params.id + `/`, taskObject).then((res) => {
            console.log(res.data)
            console.log('task a été modifié')
        }).catch((error) => {
            console.log(error)
        })

        // rederiction task list

    }

    render() {
        return (
            <div>
                <div className="d-flex align-items-center auth px-0 h-100">
                    <div className="row w-100 mx-0">
                        <div className="col-lg-4 mx-auto">
                            <div className="card text-left py-5 px-4 px-sm-5">

                                <h4>Update user</h4>
                                <form className="pt-3">
                                    <div className="form-group">
                                        <input type="text" className="form-control form-control-lg" id="exampleInputUsername1" placeholder="First name" />
                                    </div>
                                    <div className="form-group">
                                        <input type="text" className="form-control form-control-lg" id="exampleInputname" placeholder="Last name" />
                                    </div>
                                    <div className="form-group">
                                        <input type="email" className="form-control form-control-lg" id="exampleInputlastname" placeholder="Email" />
                                    </div>
                                    <div className="form-group">
                                        <input type="password" className="form-control form-control-lg" id="exampleInputEmail1" placeholder="Password" />
                                    </div>

                                    <div className="mt-3">
                                        <Link className="btn btn-block btn-primary btn-lg font-weight-medium auth-form-btn" to="/dashboard">Update</Link>
                                    </div>

                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default UpdateUser
